import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../../context/UserContext';
import { useHistory } from 'react-router-dom';

const ScrumDetails = ({ scrum }) => {
    const [tasks, setTasks] = useState([]);
    const [users, setUsers] = useState([]);
    const { user } = useContext(UserContext);
    const history = useHistory();

    useEffect(() => {
        const checkUser = () => {
            const loggedInUser = JSON.parse(localStorage.getItem('user'));
            if (!loggedInUser) {
                history.push('/login');
            }
        };
        checkUser();
    }, [history]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get(`http://localhost:4000/tasks?scrumId=${scrum.id}`);
                setTasks(response.data);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };
        fetchTasks();
    }, [scrum.id]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:4000/users');
                // If tasks exist, filter users assigned to tasks; otherwise, show all users
                const scrumUsers = tasks.length > 0
                    ? response.data.filter(user => tasks.some(task => task.assignedTo === user.id))
                    : response.data; // Show all users if no tasks are assigned
                setUsers(scrumUsers);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };
        fetchUsers();
    }, [tasks]);

    const handleStatusChange = async (taskId, newStatus) => {
        try {
            await axios.patch(`http://localhost:4000/tasks/${taskId}`, {
                status: newStatus,
                history: [
                    ...tasks.find(task => task.id === taskId).history,
                    {
                        status: newStatus,
                        date: new Date().toISOString().split('T')[0],
                    },
                ],
            });
            setTasks(prevTasks =>
                prevTasks.map(task =>
                    task.id === taskId ? { ...task, status: newStatus } : task
                )
            );
        } catch (error) {
            console.error('Error updating task status:', error);
        }
    };

    return (
        <div className="container mt-4">
            <div className="card shadow-sm">
                <div className="card-header bg-primary text-white">
                    <h3 className="mb-0">Scrum Details for {scrum.name}</h3>
                </div>
                <div className="card-body">
                    {/* Tasks Section */}
                    <h4 className="card-title mt-3 mb-3">Tasks</h4>
                    {tasks.length > 0 ? (
                        <ul className="list-group mb-4">
                            {tasks.map(task => (
                                <li 
                                    key={task.id} 
                                    className="list-group-item d-flex justify-content-between align-items-center"
                                >
                                    <div>
                                        <strong>{task.title}:</strong> {task.description} -{' '}
                                        <span 
                                            className={`badge ${
                                                task.status === 'Done' 
                                                    ? 'bg-success' 
                                                    : task.status === 'In Progress' 
                                                    ? 'bg-warning' 
                                                    : 'bg-secondary'
                                            }`}
                                        >
                                            {task.status}
                                        </span>
                                    </div>
                                    {user?.role === 'admin' && (
                                        <select
                                            value={task.status}
                                            onChange={(e) => handleStatusChange(task.id, e.target.value)}
                                            className="form-select w-auto"
                                        >
                                            <option value="To Do">To Do</option>
                                            <option value="In Progress">In Progress</option>
                                            <option value="Done">Done</option>
                                        </select>
                                    )}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <div className="alert alert-info">No tasks available</div>
                    )}

                    {/* Users Section */}
                    <h4 className="card-title mt-3 mb-3">Users</h4>
                    {users.length > 0 ? (
                        <ul className="list-group">
                            {users.map(user => (
                                <li 
                                    key={user.id} 
                                    className="list-group-item d-flex justify-content-between align-items-center"
                                >
                                    <span>{user.name}</span>
                                    <span className="text-muted">{user.email}</span>
                                    {/* Add more user details if available (e.g., role, id) */}
                                    <span className="text-muted">ID: {user.id}</span>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <div className="alert alert-info">No users available</div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ScrumDetails;
